<?phpnamespace Bfg\Doc\Attributes;use Attribute;#[Attribute(Attribute::TARGET_PROPERTY)]class Doc{    public function __construct(        public string|null $type,        public array|string|null $var_type,        public ?string $name = null,        public ?string $description = null,    ) {        $this->var_type = is_array($this->var_type) ? implode("|", $this->var_type) :            ($this->var_type ?: 'var');    }}