<?phpnamespace Bfg\Doc\Attributes;use Attribute;#[Attribute(Attribute::TARGET_PROPERTY)]class DocMethods extends Doc{    public function __construct(        public array|string|null $var_type,        public ?string $name = null,        public ?string $description = null,    ) {        parent::__construct('method', $this->var_type ?? 'var', $this->name, $this->description);    }}