<?phpnamespace Bfg\Doc\Core\Listeners;use Bfg\Doc\Core\DataForGenerate;/** * Class UpdateClassDoc * @package Bfg\Doc\Core\Listeners */class UpdateClassHelpers{    /**     * Handle the event.     *     * @param  DataForGenerate  $event     * @return void     */    public function handle(DataForGenerate $event)    {        $file = $event->data->map([$this, 'eachClass'])->implode("\n");        file_put_contents(            config('doc.help_file'),            "<?php \n\n" . $file        );    }    /**     * @param  array  $data     * @return string     */    public function eachClass(array $data): string    {        $files = [];        foreach ($data['doc_files'] as $namespace => $classes) {            foreach ($classes as $class => $lines) {                $str = str_repeat(" ", 4);                $some_class = "namespace ".($namespace ? "{$namespace} ": "")."{\n";                $some_class .= "{$str}/**\n {$str}".implode("\n {$str}", $lines)."\n {$str}*/\n";                $some_class .= "{$str}class {$class} {}\n";                $some_class .= "}";                $files[] = $some_class;            }        }        return implode("\n", $files);    }}